import React, { Component } from 'react';
import Square from './Square'
class Board extends Component {
    constructor(props) {
        super(props)
          this.state = { 
          compBoard: [
                      "", "", "", "", "",
                      "", "", "", "", "",
                      "", "", "", "", "",
                      "", "", "", "", "",
                      "", "", "", "", ""],
          userBoard:[
                      "", "", "", "", "",
                      "", "", "", "", "",
                      "", "", "", "", "",
                      "", "", "", "", "",
                      "", "", "", "", ""],
          compBoardHide:[
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪"],
          userBoardHide:[
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪",
                      "🤪", "🤪", "🤪", "🤪", "🤪"],
          userShips: 2,
          compShips: 2,
          userStatus: ""
        }
    }
    handleBoard = (index) => {
      let {compBoard,userBoard,compBoardHide,userBoardHide} = this.state
      compBoard[index] = compBoardHide[index]

    }

    resetGame = () =>{
      let {compBoard,userBoard,compBoardHide,userBoardHide,userStatus} = this.state
      compBoard = compBoard.map(value=> value ="")
      userBoard = userBoard.map(value=> value="")
      compBoardHide = compBoardHide.map(value=> value="🤪")
      userBoardHide = userBoardHide.map(value=> value="🤪")
      userStatus = ""
      this.setState({
        compBoard:compBoard,
        userBoard:userBoard,
        compBoardHide:compBoardHide,
        userBoardHide:userBoardHide,
        userStatus:userStatus,
      })

    }
//

    randomize = () => {
      // comp = computer , user = you
      let {userBoard,compBoardHide,userBoardHide} = this.state
      // finds random index that will assign battleships to compBoardHide
      let randomCompBH1 = Math.ceil(Math.random()* compBoardHide.length)
      let randomCompBH2 = Math.ceil(Math.random()* compBoardHide.length)
        while(randomCompBH1 === randomCompBH2){
          randomCompBH2 = Math.ceil(Math.random()* compBoardHide.length)
        }
        // finds random index that will assign battleships to userBoardHide
      let randomUserBH1 = Math.ceil(Math.random() * userBoardHide.length)
      let randomUserBH2 = Math.ceil(Math.random() * userBoardHide.length)
        while(randomUserBH1 === randomUserBH2){
          randomUserBH2 = Math.ceil(Math.random()* userBoardHide.length)
        }
        userBoard[randomUserBH1] = "⛵️"
        userBoard[randomUserBH2] = "⛵️"
        userBoardHide[randomUserBH1] = "⛵️"
        userBoardHide[randomUserBH2] = "⛵️"
        this.setState({
          userBoard: userBoard,
          compBoardHide:compBoardHide,
          userBoardHide:userBoardHide,
        })

    }

  render() {
    let {compBoard,userBoard,counter,userStatus} = this.state
    let compB = compBoard.map((value, index) => 
        <Square
          handleBoard = {this.handleBoard}
          randomize = {this.randomize}
          index = { index } 
          value = { value }
          counter = {counter}
          userStatus = {userStatus}
        /> )
    let userB = userBoard.map((value, index)=> 
        <Square 
          index = {index}
          value = {value}
          handleBoard = {this.handleBoard}
        />
        )

    return (
     <div>
       <p>Computer Board</p>
       <br/>
        <div className = "board">
          {compB}
        </div>
        <p>User Board</p>
        <br/>
        <div className = "board">
          {userB}
        </div>
        <button onClick={this.randomizer}>Start Game</button>
        <button onClick={this.resetGame}>Reset Game</button>
      </div>
    );
  }
}

export default Board
